mapper.select:
	select distinct(agent_id) from (
       select distinct(agent_id) from order_ticket 
       where 
        trade_time  >=#{startDate}     and trade_time < #{endDate}
   union all
      select distinct(agent_id) from order_ticket_his 
      where 
       trade_time  >=#{startDate}     and trade_time < #{endDate}
    ) as a

select 
from order_ticket where agent_id= #{agentId} and   trade_time  >=#{startDate}     and trade_time < #{endDate} 
union all
select 
from order_ticket_his where agent_id= #{agentId} and   trade_time  >=#{startDate}     and trade_time < #{endDate} 
ORDER BY agent_id DESC

        select if(trade_type=1,sum(trade_price),0) as trade_price from (
	    select 
	    from order_ticket where agent_id= #{agentId} and   trade_time  >=#{startDate}     and trade_time < #{endDate} 
	    union all
	    select 
	    from order_ticket_his where agent_id= #{agentId} and   trade_time  >=#{startDate}     and trade_time < #{endDate} 
	    )as a

select sum(recyclePrice) as recyclePrice from ( select
from order_ticket where ticketInfo_id in
#{item.ticketInfoId}
union all select
from order_ticket_his where ticketInfo_id in
#{item.ticketInfoId}
) as a

mapper.sql:
where 1=1


	    and i.tkId =  #{tkId}


	    and i.uid =  #{uid}


	    and t.agent_id =  #{agentNum}


